mapper.select:
SELECT

  ${returnFields}




FROM wm_user

    AND id = #{id,jdbcType=BIGINT}

        #{item,jdbcType=BIGINT}

    AND account = #{account,jdbcType=VARCHAR}
    AND account LIKE concat(#{accountLike,jdbcType=VARCHAR},'%')
    AND open_id = #{openId,jdbcType=VARCHAR}
    AND open_id LIKE concat(#{openIdLike,jdbcType=VARCHAR},'%')
    AND session_key = #{sessionKey,jdbcType=VARCHAR}
    AND session_key LIKE concat(#{sessionKeyLike,jdbcType=VARCHAR},'%')

        #{item,jdbcType=VARCHAR}

    AND name = #{name,jdbcType=VARCHAR}
    AND name LIKE concat(#{nameLike,jdbcType=VARCHAR},'%')
    AND code = #{code,jdbcType=VARCHAR}
    AND code LIKE concat(#{codeLike,jdbcType=VARCHAR},'%')
    AND password = #{password,jdbcType=VARCHAR}
    AND password LIKE concat(#{passwordLike,jdbcType=VARCHAR},'%')
    AND mobile = #{mobile,jdbcType=VARCHAR}
    AND mobile LIKE concat(#{mobileLike,jdbcType=VARCHAR},'%')

        #{item,jdbcType=VARCHAR}

    AND create_time >= #{createTimeFrom,jdbcType=TIMESTAMP}
    AND create_time <= #{createTimeTo,jdbcType=TIMESTAMP}
    AND update_time >= #{updateTimeFrom,jdbcType=TIMESTAMP}
    AND update_time <= #{updateTimeTo,jdbcType=TIMESTAMP}
    AND u_status = #{uStatus,jdbcType=TINYINT}

        #{item,jdbcType=TINYINT}



    order by ${orderBy} ${order}
    order by id desc
